(188, 1, '2022-09-28 14:51:45', '2022-09-28 14:51:45', '<!-- wp:media-text {\"mediaId\":205,\"mediaType\":\"image\",\"mediaWidth\":25} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:25% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"https://ebohrman.bitlampsites.com/wpd/homework/wp-content/uploads/2022/09/dawnlands.jpeg\" alt=\"Dawnlands Book Cover\" class=\"wp-image-205 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading -->\n<h2>Dawnlands</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>The Fairmile Series</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>The new historical novel from Philippa Gregory, the Number One bestselling author of&nbsp;<em>Tidelands</em>&nbsp;and&nbsp;<em>Dark Tides</em>.</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>It is 1685, England is on the brink of a renewed civil war against the Stuart kings and many families are bitterly divided.&nbsp;</strong>The spellbinding Fairmile series continues as the fiercely independent Alinor and her family find themselves entangled in palace intrigue, political upheaval, and life-changing secrets in seventeenth-century England.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong><em>Dawnlands</em>&nbsp;will be out on 8 November 2022.</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"style\":{\"typography\":{\"fontSize\":\"10px\"},\"border\":{\"radius\":\"0px\"},\"spacing\":{\"padding\":{\"top\":\"6px\",\"right\":\"6px\",\"bottom\":\"6px\",\"left\":\"6px\"}},\"color\":{\"background\":\"#402b19\"}},\"className\":\"is-style-fill\"} -->\n<div class=\"wp-block-button has-custom-font-size is-style-fill\" style=\"font-size:10px\"><a class=\"wp-block-button__link has-background\" style=\"border-radius:0px;background-color:#402b19;padding-top:6px;padding-right:6px;padding-bottom:6px;padding-left:6px\">PRE-ORDER</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:advgb/container -->\n<div class=\"wp-block-advgb-container advgb-blocks-container\"></div>\n<!-- /wp:advgb/container -->\n\n<!-- wp:advgb/accordions {\"changed\":true,\"rootBlockId\":\"a219ffde-ba8a-4500-b4c2-84df73d6767b\"} -->\n<div class=\"wp-block-advgb-accordions advgb-accordion-wrapper\"></div>\n<!-- /wp:advgb/accordions -->', 'Home Page', '', 'publish', 'closed', 'closed', '', 'home-page', '', '', '2022-10-06 23:17:13', '2022-10-06 23:17:13', '', 0, 'https://ebohrman.bitlampsites.com/wpd/homework/?page_id=188', 0, 'page', '', 0),
